import { Component, OnInit, Injectable } from '@angular/core';

import { AdService } from './ad.service';
import { ClassificationType } from './ad-item';
import { ClassificationService } from './classification.service';


@Component({
  selector: 'app-root',
  template: `
       <div class="classification-banner" data-classification="classification">
   
    <pre>&lt;div class="classification-banner" data-classification="UNCLASSIFIED"&gt;
    &lt;!--  {{classification}} --&gt;
&lt;/div&gt;</pre>
  </div>
  `,
   providers: [ClassificationService]
})


export  class AppComponent implements OnInit {
 

  constructor(private classificationService: ClassificationService) {}

  ngOnInit() {
    this.classificationService.fetchClassification("UNCLASSIFIED")
  }
  get classification() { return this.classificationService.classification ; }
}

