import { Component, Input } from '@angular/core';

import { ClassificationComponent } from './ad.component';

@Component({
  template: `
    <div class="job-ad">
      <h4>{{data.text}}</h4>
    </div>
  `,
})
export class HeroJobAdComponent implements ClassificationComponent {
  @Input() data: any;
}

